/*global $ */

epx = window.epx || {};
epx.library = window.epx.library || {};

epx.library.breadcrumbs = (function () {
    var loading = false;
    function LoadAndShowBreadcrumbDropDown(href, object) {
        var $menu = $('#breadcrumbDropDownMenu');
        if ($menu.is(':empty') && !loading) {
            loading = true;
            var href = epx.library.toc.buildTocHref(href);
            $.ajax({
                type: "GET",
                async: true,
                url: href,
                cache: false,
                dataType: "json",
                success: function (r) {
                    if (!r || r.length < 1) {
                        window.epx.utilities.log("TOC web service returned 0 nodes.");
                        return;
                    }

                    window.epx.utilities.log("TOC web service returned " + r.length + " node(s), processing...");
                    var currentTitle = object.attr("title").trim();
                    var html = "<ul>";

                    for (var i = 0; i < r.length; i++) {
                        if (r[i].Title.trim() == currentTitle) {
                            html += "<li class=\"current\">";
                        } else {
                            html += "<li>";
                        }

                        html += "<a href=\"" + r[i].Href + "\" Title=\"" + r[i].ToolTip + "\">" + window.epx.utilities.htmlEncode(r[i].Title) + "</a>"

                        html += "</li>";
                    }

                    html += "</ul>";

                    $menu.append(html);

                    loading = false;

                    ShowBreadcrumbDropDown(object, "breadcrumbDropDownMenu");
                },
                error: function () {
                    window.epx.utilities.log("TOC web service returned error.");
                    loading = false;
                }
            });
        } else {
            ShowBreadcrumbDropDown(object, "breadcrumbDropDownMenu");
        }
    }

    function ShowBreadcrumbDropDown(object, menuId) {
        var $menu = $("#" + menuId);
        var pos = object.position();
        var width = object.outerWidth();
        var height = object.outerHeight();
        var menuWidth = $menu.outerWidth();
        var windowWidth = $("div#breadcrumbs").width();
        var intent = 15;

        if ($("html").attr("dir") == "rtl") {
            $menu.css({
                top: (pos.top + height) + "px",
                right: Math.max((windowWidth - pos.left - menuWidth), intent) + "px"
            });
        } else {
            $menu.css({
                top: (pos.top + height) + "px",
                left: Math.max((pos.left + width - menuWidth), intent) + "px"
            });
        }
        $menu.animate({ height: "toggle" }, 66, 'swing', function () {
            $menu.css({
                display: "block",
            });
        });
    }

    function HideBreadcrumbDropDown() {
        $("#breadcrumbDropDownMenu").hide();
        $("#tocDropDownMenu").hide();
        $("#tocPopupMenu").hide();
        $('div#content').css("display", "");
        $('#breadcrumbMobile').css("display", "");
        $('div#rightNavigationMenuThumbnail').css("display", "");
    }

    function ShowTocPopup(object) {
        $('#tocPopupMenu').show();
        $('#breadcrumbMobile').hide();
        $('div#content').hide();
        $('div#rightNavigationMenuThumbnail').hide();
    }

    return {
        LoadAndShowBreadcrumbDropDown: LoadAndShowBreadcrumbDropDown,
        ShowBreadcrumbDropDown: ShowBreadcrumbDropDown,
        HideBreadcrumbDropDown: HideBreadcrumbDropDown,
        ShowTocPopup: ShowTocPopup
    }
})();

$(document).ready(function () {
    $('#breadcrumbTablet').append($('#navigationButtons'));

    $("#breadcrumbDropDownButton").bind('click',
        function (e) {
            var targetHref = $(this).attr("targetHref");
            epx.library.breadcrumbs.LoadAndShowBreadcrumbDropDown(targetHref, $(this));
            e.stopPropagation();
            return false;
        }
    );

    $("#tocDropDownButton").bind('click',
        function (e) {
            //clean the style
            $("#tocnav").removeAttr('style');
            epx.library.breadcrumbs.ShowBreadcrumbDropDown($(this), "tocDropDownMenu");
            e.stopPropagation();
            return false;
        }
    );

    $("#tocPopupButton").bind('click',
        function (e) {
            //clean the style
            $("#tocnav").removeAttr('style');
            epx.library.breadcrumbs.ShowTocPopup($(this));
            e.stopPropagation();
            return false;
        }
    );

    $("#breadcrumbDropDownMenu, #tocDropDownMenu").bind('click',
        function (e) {
            e.stopPropagation();
            return true
        }
    );

    $("#page").bind('click', function () {
            if (epx.library.responsive.getCurrentViewType() != epx.library.responsive.viewTypes.Mobile) {
                epx.library.breadcrumbs.HideBreadcrumbDropDown();
            }
        }
    );

    $("#tocPopMenuClose").bind('click', function () {
            epx.library.breadcrumbs.HideBreadcrumbDropDown();
        }
    );
});;
